    },
    {
      id: 3,
      title: "ألوان",
      img: "/assets/images/bblash-logo-copy.png",
    },
    {
      id: 3,
      title: "ألوان",
      img: "/assets/images/bblash-logo-copy.png",
    },
    // Add more items as needed
  ];

  useEffect(() => {
    const swiper = swiperRef.current?.swiper;

    if (swiper && swiper.navigation) {
      // Assign navigation buttons after swiper is fully initialized
      swiper.params.navigation.prevEl = prevRef.current;
      swiper.params.navigation.nextEl = nextRef.current;

      swiper.navigation.init();
      swiper.navigation.update();
    }
  }, []);



    return (
      <div className="bg-class-sliders" style={{ position: "relative" }}>
        <h2>عملائنا الكرام</h2>
        <Swiper
          modules={[Navigation, Pagination, Autoplay]}
          spaceBetween={200}
          slidesPerView={4}
          loop={true}
          autoplay={{ delay: 3000 }}
          pagination={{ clickable: true }}
          onBeforeInit={(swiper) => {
            // Assign navigation buttons dynamically
            swiper.params.navigation.prevEl = prevRef.current;
            swiper.params.navigation.nextEl = nextRef.current;
          }}
          onSwiper={(swiper) => {
            // Initialize and update navigation
            setTimeout(() => {
              swiper.navigation.init();
              swiper.navigation.update();
            });
          }}
        >
          {brands.map((brand) => (
            <SwiperSlide key={brand.id}>
              <div className="brand-1">
                <Link to={`/brand-detil/${brand.id}`}>
                  <img
                    src={brand.img}
                    onError={(e) => {
                      e.target.onerror = null;
                      e.target.src = "/assets/images/logo-wb-2048x998.png"; // Fallback to default image
                    }}
                    alt={brand.title}
                  />
                </Link>
              </div>
            </SwiperSlide>
          ))}
        </Swiper>

        {/* Navigation buttons */}
        <div className="slider-nav">
          <button ref={prevRef} className="prev-slide">
            <svg className="btn-prev btn-prev-black">
              <use xlinkHref="#arrow-left"></use>
            </svg>
          </button>
          <button ref={nextRef} className="next-slide">
            <svg className="btn-next btn-next-black">
              <use xlinkHref="#arrow-right"></use>
            </svg>
          </button>
        </div>
      </div>
  );
};

export default SliderCamp;
