import React, { useEffect, useState } from "react";
import { Swiper, SwiperSlide } from "swiper/react";

import "animate.css";
import "./main.css";
import {
  Navigation,
  Pagination,
  Autoplay,
  FreeMode,
  Thumbs,
} from "swiper/modules";
import "swiper/css";
import "swiper/css/navigation";
import "swiper/css/pagination";
import "swiper/css/thumbs";
import "swiper/css/free-mode";
const Main = () => {
  const [visibleIndex, setVisibleIndex] = useState(-1); // متغير لتخزين الفهرس الخاص بالصورة المرئية
  const [imgAn, setImgAn] = useState(""); // متغير لتخزين الإنميشن
  const [thumbsSwiper, setThumbsSwiper] = useState(null);
  // قائمة الصور
  const images = [
    process.env.PUBLIC_URL + "/assets/images/جرافيك.png",
    process.env.PUBLIC_URL + "/assets/images/سوشيال-ميديا.png",
    process.env.PUBLIC_URL + "/assets/images/Group-12.png",
  ];

  useEffect(() => {
    const timer = setInterval(() => {
      setVisibleIndex((prev) => prev + 1); // زيادة الفهرس لجعل الصورة التالية مرئية
    }, 100); // كل صورة ستظهر بعد 1000 ملي ثانية (1 ثانية)

    // تعيين الإنميشن للصورة الإضافية بعد 2 ثانية
    const liftTimer = setTimeout(() => {
      setImgAn("animate__lightSpeedInLeft");
    }, 0); // بعد 2000 ملي ثانية (2 ثانية)

    // تنظيف المؤقت عند فك التعلق
    return () => {
      clearInterval(timer);
      clearTimeout(liftTimer);
    };
  }, []);

  return (
      <Swiper
        modules={[Navigation, Pagination, Autoplay, Thumbs]}
        // navigation
        // loop={true}
        slidesPerView={1}
        pagination={{ clickable: true }}
        // autoplay={{ delay: 3000 }}
        thumbs={{ swiper: thumbsSwiper }}
      >
        <SwiperSlide>
    <main className="main-class">

          <div className="flex">
            {images.map((src, index) => (
              <div
                key={index}
                className={`animate__animated ${
                  visibleIndex >= index ? "animate__backInDown" : ""
                }`}
              >
                {visibleIndex >= index && (
                  <img
                    src={src}
                    alt={`Image ${index + 1}`}
                    width={300}
                    style={{
                      display: visibleIndex >= index ? "block" : "none",
                    }} // إظهار الصورة فقط إذا كانت مرئية
                  />
                )}
              </div>
            ))}
            <div className="img-foote-smoll">
              <img src="/assets/images/4.png" alt="" />
            </div>
            <div className={`animate__animated ${imgAn} img-lift-class`}>
              <img
                src={process.env.PUBLIC_URL + "/assets/images/Group-4.png"}
                alt=""
              />
            </div>
            <div className={`animate__animated ${imgAn} img-right-class`}>
              <img
                src={process.env.PUBLIC_URL + "/assets/images/Group-4.png"}
                alt=""
              />
            </div>
          </div>
    </main>

        </SwiperSlide>
        <SwiperSlide>
          <div className="flex img-bg-class">

          </div>
        </SwiperSlide>
        <SwiperSlide>
          <div className="flex">

            slider 3
            </div>
        </SwiperSlide>

      </Swiper>
  );
};

export default Main;
