import React, { useEffect, useState } from 'react';
import "animate.css";
import "./main.css";

const Main = () => {
  const [animateClasses, setAnimateClasses] = useState(['', '', '']); // مصفوفة لتخزين الفئات المتحركة لكل صورة
  const [visible, setVisible] = useState([false, false, false]); // مصفوفة للتحكم في رؤية كل صورة

  useEffect(() => {
    const timers = []; // مصفوفة لتخزين مؤقتات setTimeout
    const delay = 500; // تأخير لكل صورة (بالمللي ثانية)

    // حلقة لتعيين الفئة المتحركة لكل صورة مع تأخير
    animateClasses.forEach((_, index) => {
      timers.push(
        setTimeout(() => {
          setVisible((prev) => {
            const newVisible = [...prev];
            newVisible[index] = true; // جعل الصورة مرئية
            return newVisible;
          });

          // إضافة الفئة المتحركة بعد فترة قصيرة من جعل الصورة مرئية
          setTimeout(() => {
            setAnimateClasses((prevClasses) => {
              const newClasses = [...prevClasses];
              newClasses[index] = 'animate__animated animate__backInDown'; // إضافة الفئة المتحركة
              return newClasses;
            });
          }, 100); // تأخير 100 مللي ثانية لإظهار الانميشن بعد جعل الصورة مرئية
        }, index * delay) // تأخير مختلف لكل صورة
      );
    });

    // تنظيف المؤقتات عند فك التعلق
    return () => {
      timers.forEach((timer) => clearTimeout(timer));
    };
  }, []);

  return (
    <main className="main-class"> 
      <div className='flex'>
        {animateClasses.map((animateClass, index) => (
          <div key={index} className={animateClass} style={{ opacity: visible[index] ? 1 : 0 }}>
            <img 
              src={process.env.PUBLIC_URL + '/assets/images/Group-12.png'} 
              alt="" 
              width={250} 
              height={200} 
              style={{ display: visible[index] ? 'block' : 'none' }} // التحكم في عرض الصورة
            />
          </div>
        ))}
      </div>
    </main>
  );
}

export default Main;
