import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import "./header.css";

const Header = () => {
  const [showModal, setShowModal] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      const section = document.querySelector("nav");
      if (window.scrollY > 0) {
        section.classList.add("scrolled");
      } else {
        section.classList.remove("scrolled");
      }
    };

    window.addEventListener("scroll", handleScroll);

    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  // Add/remove body class to disable scrolling when modal is open
  useEffect(() => {
    if (showModal) {
      document.body.classList.add("modal-open");
    } else {
      document.body.classList.remove("modal-open");
    }
  }, [showModal]);

  return (
    <header>
      <nav>
        {/* Existing nav content */}
      </nav>
      <section style={{ display: "flex" }}>
        <div className="link-route">
          {/* Existing links */}
        </div>
        <div className="icone-header-social">
          {/* Social icons */}
        </div>

        <div>
          <button className="icon-menu" onClick={() => setShowModal(true)}>
            <svg
              width="24"
              height="24"
              viewBox="0 0 24 24"
              fill="none"
              xmlns="https://www.w3.org/2000/svg"
            >
              <rect x="3" y="6" width="18" height="2" fill="black" />
              <rect x="3" y="11" width="18" height="2" fill="black" />
              <rect x="3" y="16" width="18" height="2" fill="black" />
            </svg>
          </button>

          {showModal && (
            <div className="fixed">
              <ul className={showModal ? "modal" : "modal element-close"}>
                <li>
                  <button
                    className="icon-x"
                    onClick={() => setShowModal(false)}
                  >
                    x
                  </button>
                </li>
                <li onClick={() => setShowModal(false)}>
                  <Link to="/">Home</Link>
                </li>
                <li onClick={() => setShowModal(false)}>
                  <Link to="/about">About</Link>
                </li>
                <li onClick={() => setShowModal(false)}>
                  <Link to="/contact">Contact</Link>
                </li>
                <li onClick={() => setShowModal(false)}>
                  <Link to="/blog">Blog</Link>
                </li>
                <div className="dropdown-con">{/* Language Dropdown */}</div>
              </ul>
            </div>
          )}
        </div>
      </section>
    </header>
  );
};

export default Header;
