import React, { useState, useRef } from "react";
import { Swiper, SwiperSlide } from "swiper/react";
import { Navigation, Pagination, Autoplay } from "swiper/modules";
import { useTranslation } from "react-i18next";
import { Link } from "react-router-dom";
// Import Swiper styles
import "swiper/css";
import "swiper/css/navigation";
import "swiper/css/pagination";
import "swiper/css/scrollbar";
import "./slider-camp.css";

const SliderCamp = () => {
  const { i18n } = useTranslation();
  const swiperRef = useRef(null); // Create a ref for the Swiper instance

  const brands = [
    {
      id: 1,
      title: "جرافيك",
      img: "/assets/images/2024-03-23-65feb16b13e9d.png",
    },
    {
      id: 2,
      title: "سوشيال ميديا",
      img: "/assets/images/logo-wb-2048x998.png",
    },
    {
      id: 3,
      title: "ألوان",
      img: "/assets/images/bblash-logo-copy.png",
    },
    // Add the rest of your brands here...
  ];

  return (
    <div className="bg-class-sliders" style={{ position: "relative" }}>
      <Swiper
        modules={[Navigation, Pagination, Autoplay]}
        spaceBetween={20}
        slidesPerView={5}
        navigation
        scrollbar={{ draggable: true }}
        loop={true}
        onSwiper={(swiper) => (swiperRef.current = swiper)} // Assign Swiper instance to ref
      >
        {brands.map((brand) => (
          <SwiperSlide key={brand.id}>
            <div className="brand-1">
              <Link to={`/brand-detil/${brand.id}`}>
                <img
                  src={brand.img}
                  onError={(e) => {
                    e.target.onerror = null;
                    e.target.src = "/assets/images/logo-wb-2048x998.png"; // Fallback to default image if error occurs
                  }}
                  alt={brand.title}
                />
              </Link>
            </div>
          </SwiperSlide>
        ))}
      </Swiper>

      {/* أزرار التنقل */}
      <div className="slider-nav">
        <button
          className="prev-slide"
          onClick={() => swiperRef.current?.slidePrev()} // Move to the previous slide
        >
          السابق
        </button>
        <button
          className="next-slide"
          onClick={() => swiperRef.current?.slideNext()} // Move to the next slide
        >
          التالي
        </button>
      </div>
    </div>
  );
};

export default SliderCamp;
