import React, { useEffect, useState } from 'react';
import "animate.css";
import "./main.css";

const Main = () => {
  const [visibleIndex, setVisibleIndex] = useState(-1); // متغير لتخزين الفهرس الخاص بالصورة المرئية

  // قائمة الصور
  const images = [
    process.env.PUBLIC_URL + '/assets/images/جرافيك.png', // استبدلها بمسارات صورك
    process.env.PUBLIC_URL + '/assets/images/سوشيال-ميديا.png',
    process.env.PUBLIC_URL + '/assets/images/Group-12.png',
  ];

  useEffect(() => {
    const timer = setInterval(() => {
      setVisibleIndex((prev) => prev + 1); // زيادة الفهرس لجعل الصورة التالية مرئية
    }, 100); // كل صورة ستظهر بعد 1000 ملي ثانية (1 ثانية)

    // تنظيف المؤقت عند فك التعلق
    return () => clearInterval(timer);
  }, []);

  return (
    <main className="main-class"> 
      <div className='flex'>
        {images.map((src, index) => (
          <div key={index} className={`animate__animated ${visibleIndex >= index ? 'animate__backInDown' : ''}`} >
            {visibleIndex >= index && (
              <img 
                src={src} 
                alt={`Image ${index + 1}`} 
                width={300}
                style={{ display: visibleIndex >= index ? 'block' : 'none' }} // إظهار الصورة فقط إذا كانت مرئية
              />
            )}
          </div>
        ))}
        <div className='animate__lightSpeedInLeft .img-lift-class'>
          <img src="/assets/images/Group-4.png" alt="" />
        </div>
      </div>
    </main>
  );
}

export default Main;
